targets.expr:
sum by (instance) (
  irate(node_network_receive_bytes_total{
    job="node", instance="$instance",
    device=~"^(enp|ens|eno|eth|bond|wlp).*"
  }[$__rate_interval]) * 8
)

sum by (instance) (
  irate(node_network_transmit_bytes_total{
    job="node", instance="$instance",
    device=~"^(enp|ens|eno|eth|bond|wlp).*"
  }[$__rate_interval]) * 8
)

sum(
  increase(
    (
      label_replace(
        sum by (stream_type, stream_file_resolution) (
          play_seconds_total{job=~"$job", instance=~"$plex_instance", server=~"$server", stream_type!=""}
        ),
        "res","2160",
        "stream_file_resolution","(?i)^4k$"
      )
      or label_replace(
        sum by (stream_type, stream_file_resolution) (
          play_seconds_total{job=~"$job", instance=~"$plex_instance", server=~"$server", stream_type!=""}
        ),
        "res","480",
        "stream_file_resolution","(?i)^sd$"
      )
      or label_replace(
        sum by (stream_type, stream_file_resolution) (
          play_seconds_total{job=~"$job", instance=~"$plex_instance", server=~"$server", stream_type!=""}
        ),
        "res","$1p",
        "stream_file_resolution","^([0-9]+)$"
      )
      or label_replace(
        sum by (stream_type, stream_file_resolution) (
          play_seconds_total{job=~"$job", instance=~"$plex_instance", server=~"$server", stream_type!=""}
        ),
        "res","$1",
        "stream_file_resolution",".*?([1-9][0-9]{2,3}).*"
      )
      or label_replace(
        sum by (stream_type, stream_file_resolution) (
          play_seconds_total{job=~"$job", instance=~"$plex_instance", server=~"$server", stream_type!=""}
        ),
        "res","unknown",
        "stream_file_resolution","^$"
      )
    )
  [$__interval:])
) by (stream_type, res)

